	public static int[] solve(int[] A, int[] B) {
		int[] result = new int[B.length];
		int[] dp = new int[A.length];
		for(int j = 1; j<A.length;j++) {
			dp[j] = A[j] + A[j-1]; 
		}
		for (int i = 0; i < B.length; i++) {
			if (B[i] < dp[0]) {
				result[i] = 0;
				continue;
			} else {
				result[i] = posibbleCombination(dp, B[i]);
			}
		}
		return result;
	}

	static int posibbleCombination(int[] dp, int x) {
		int i = 0;
		while (i<dp.length) {
			if(dp[i]<=x) {
				i++;
			} else {
				break;
			}
		}
		System.out.println(i);
		return i;
	}
